import { addSplideClasses, connectSplideArrows, connectSplideBullets, connectSplideCarouselBullets, sel, selAll, splideAutoWidth } from './utils'
import '@splidejs/splide/css'
import Splide from '@splidejs/splide'
import { AutoScroll } from '@splidejs/splide-extension-auto-scroll'

import { Intersection } from '@splidejs/splide-extension-intersection'

export default function Home() {
  function osInit() {
    const name = 'os'
    addSplideClasses(name + '__slider')
    const splide = new Splide('.' + name + '__slider', {
      // perPage: 1,
      // width: '100%',
      autoplay: false,
      // autoHeight: true,
      type: 'fade',
      rewind: true,
      pagination: false,
      arrows: false,
    })

    addSplideClasses(name + '__thumb-slider')
    const thumbnails = new Splide('.' + name + '__thumb-slider', {
      // gap: '0',
      // direction: 'ttb',
      // height: '35rem',
      // autoHeight: true,
      // perPage: 1,
      rewind: true,
      pagination: false,
      arrows: false,
      isNavigation: true,
      // speed: 1500,
      // easing: 'cubic-bezier(0.16, 1, 0.3, 1)',
      breakpoints: {
        478: {
          perPage: 1,
          gap: '0.5rem',
          // autoWidth: true,
        },
      },
    })
    splide.sync(thumbnails)
    splide.mount()
    thumbnails.mount()
    connectSplideBullets(splide, '.bullets.is--os')
    // connectSplideArrows(splide, 'arrows', '.round-arrow')
  }
  osInit()

  function newsInit() {
    const name = 'news'
    addSplideClasses(name + '__slider')
    const splide = new Splide('.' + name + '__slider', {
      perPage: 2,
      gap: '1.5rem',
      // autoplay: false,
      // autoHeight: true,
      // type: 'fade',
      rewind: true,
      pagination: false,
      arrows: false,
      speed: 1500,
      padding: { right: '20%' },
      easing: 'cubic-bezier(0.16, 1, 0.3, 1)',
      breakpoints: {
        768: {
          padding: { right: '0' },
          gap: '0.5rem',
        },
        467: {
          perPage: 1,
        },
      },
    })

    connectSplideCarouselBullets(splide, '.bullets.is--news')
    splide.mount()
  }
  newsInit()
  function testimonialsInit() {
    const name = 'testimonials'
    addSplideClasses(name + '__slider')
    const splide = new Splide('.' + name + '__slider', {
      // perPage: 1,
      // width: '100%',
      autoplay: false,
      // autoHeight: true,
      type: 'fade',
      rewind: true,
      pagination: false,
      arrows: false,
    })

    addSplideClasses(name + '__img-slider')
    const img = new Splide('.' + name + '__img-slider', {
      gap: '1rem',
      // direction: 'ttb',
      // height: '35rem',
      // autoHeight: true,
      // perPage: 1,
      // rewind: true,
      pagination: false,
      arrows: false,
      // isNavigation: true,
      speed: 1500,
      easing: 'cubic-bezier(0.16, 1, 0.3, 1)',
      breakpoints: {
        478: {
          perPage: 1,
          gap: '0.5rem',
        },
      },
    })
    splide.sync(img)
    splide.mount()
    img.mount()
    connectSplideBullets(splide, '.bullets.is--' + name)
  }
  testimonialsInit()
  function galleryInit() {
    const classPrefix = 'cta'
    addSplideClasses(classPrefix + '__slider')
    const slider = new Splide(sel(`.${classPrefix}__slider`), {
      arrows: false,
      pagination: false,
      gap: '2.5rem',
      type: 'loop',
      autoWidth: true,
      autoScroll: { speed: 0.6, autoStart: false },
      breakpoints: {
        767: {
          gap: '1rem',
        },
      },
    })

    splideAutoWidth(slider)
    slider.mount({ AutoScroll })
  }
  galleryInit()
}
